custom_css:
:root {
  --custom-theme-style-inputs: [
  {
    "type": "color",
    "varId": "NSDSmartThemeBorderColor",
    "displayText": "Theme Background",
    "default": "rgba(32, 33, 36, 1)"
  },
  {
    "type": "color",
    "varId": "NSDDrawer-iconColor",
    "displayText": "Drawer icons Color",
    "default": "rgba(214, 214, 214, 1)"
  },
  {
    "type": "color",
    "varId": "NSDsmartThemeHiglitesColor",
    "displayText": "Theme Higlites Color",
    "default": "rgba(32, 32, 32, 0.75)"
  },
  {
    "type": "select",
    "varId": "NSDexpressionWidth",
    "displayText": "Expression Visibility",
    "default": "512px",
    "options": [
      {
        "label": "visible",
        "value": "512px"
      },
      {
        "label": "hidden",
        "value": "0px"
      }
    ]
  },
  {
    "type": "slider",
    "varId": "NSDlistGrid-char-panel-width",
    "displayText": "Grid char panel width",
    "default": "580",
    "min": 240,
    "max": 740,
    "step": 1
  },
  {
    "type": "slider",
    "varId": "NSDnormal-char-panel-width",
    "displayText": "Normal char panel width",
    "default": "420",
    "min": 240,
    "max": 740,
    "step": 1
  },
  {
    "type": "slider",
    "varId": "NSDbgImageOpacity",
    "displayText": "Bg Image Opacity",
    "default": "0.5",
    "min": 0,
    "max": 1,
    "step": 0.01
  },
  {
    "type": "slider",
    "varId": "NSDAnimation-duration",
    "displayText": "Animation duration",
    "default": "0.4",
    "min": 0,
    "max": 2,
    "step": 0.01
  }
]
}
