#options {
	display: block !important;
	border-top: 8px groove var(--SmartThemeQuoteColor);
	height: 0px;
	margin: 0px 0px 2px 0px !important;
	border-radius: 0;
	width: 2.2em;
	transition: 0s;
	overflow-x: auto;
}

#options:hover {
	height: auto !important;
	width: auto !important;
}

.mes .mes_buttons {
	visibility: hidden;
	opacity: 0;
}
.mes:hover .mes_buttons {
  visibility: visible;
  opacity: 1;
}

#send_form:hover #qr--bar {
	height: 40px; 
	transition: 0.1s;
}

#qr--bar{
transition: 0.1s;
border-top: 6px solid var(--SmartThemeQuoteColor);
height: 4px;
}

/* 
  For better quality of char img in chat edit config.yaml -> thumbnails:
  enabled: false

  Caution for people with thousands of chars! it will make all thairs img load in full size in Character List Panel!
*/

.mes {
  padding: 0 !important;
  overflow-y: clip;
}

.mes_block {
  z-index: 1;
  margin: 12px !important;
}

.mesIDDisplay {
  display: none !important;
}

.mes[is_user="false"] .mesAvatarWrapper {
  position: absolute !important;
  top: 0 !important;
  right: 0 !important;
}

.mes[is_user="false"] .mes_block {
  margin-right: calc(var(--avatar-base-width) * var(--big-avatar-width-factor) * 3) !important;
}

.mes[is_user="false"] .mesAvatarWrapper .avatar {
  width: calc(var(--avatar-base-width) * var(--big-avatar-width-factor) * 4) !important;
  height: calc(var(--avatar-base-height) * var(--big-avatar-height-factor) * 3) !important;
  -webkit-mask-image:
    linear-gradient(to right, rgba(0, 0, 0, 0) 0%, black 40%),
    linear-gradient(to top, rgba(0, 0, 0, 0) 0%, black 40%);
  mask-image:
    linear-gradient(to right, rgba(0, 0, 0, 0) 0%, black 40%),
    linear-gradient(to top, rgba(0, 0, 0, 0) 0%, black 40%);

  -webkit-mask-composite: destination-in;
  mask-composite: intersect;
  mask-size: 100% 100%;
  mask-repeat: no-repeat;
  border-radius: unset !important;
}

.mes[is_user="true"] .mesAvatarWrapper {
  z-index: 0;
  position: absolute !important;
  top: 0 !important;
  left: 0 !important;
}

.mes[is_user="true"] .mes_block {
  margin-left: calc(var(--avatar-base-width) * var(--big-avatar-width-factor) * 3) !important;
}

.mes[is_user="true"] .mesAvatarWrapper .avatar {
  width: calc(var(--avatar-base-width) * var(--big-avatar-width-factor) * 4) !important;
  height: calc(var(--avatar-base-height) * var(--big-avatar-height-factor) * 3) !important;
  -webkit-mask-image:
    linear-gradient(to left, rgba(0, 0, 0, 0) 0%, black 40%),
    linear-gradient(to top, rgba(0, 0, 0, 0) 0%, black 40%);
  mask-image:
    linear-gradient(to left, rgba(0, 0, 0, 0) 0%, black 40%),
    linear-gradient(to top, rgba(0, 0, 0, 0) 0%, black 40%);

  /* Ensure proper masking behavior */
  -webkit-mask-composite: destination-in;
  mask-composite: intersect;
  mask-size: 100% 100%;
  mask-repeat: no-repeat;
  border-radius: unset !important;
}

.mesAvatarWrapper .avatar img {
  position: absolute;
  width: calc(var(--avatar-base-width) * var(--big-avatar-width-factor) * 4) !important;
  height: calc(var(--avatar-base-height) * var(--big-avatar-height-factor) * 4) !important;

}

.edit_textarea,
.text_pole,
.textarea_compact,
code,
#description_textarea,
#firstmessage_textarea,
.csss--contentm {
	font-family: Consolas, monospace !important;
}
.world_entry_form_control textarea{
	font-family: Consolas, monospace !important;
}

.mes_edit.fa-solid.fa-pencil {
	content: "\f303";
	font-size: x-large;
}

.mes_buttons, .extraMesButtons {
	display: contents;
}

/*
NoShadowDribbblish

Css theme for SillyTavern
Made by: IceFog72
Feedback discord: https://discord.gg/2tJcWeMjFQ

https://github.com/IceFog72/ST-NoShadowDribbblish

Based on: AlmostDribbblish(IceFog72) -> Basic Discord Layout(IceFog72) -> st-discord-inspired(nyxkrage)


It is advisable to use for customizing the theme:
https://github.com/IceFog72/SillyTavern-CustomThemeStyleInputs
and theme json with configurated settings: NoShadowDribbblish LunarV2

MIT License (st-discord-inspired)

*/
:root {
  --NSDlistGrid-char-panel-width: 580;
  --NSDnormal-char-panel-width: 420;
  --NSDAnimation-duration: 0.01;
  --NSDexpressionVisibility: collapse;
  --NSDsmartThemeHiglitesColor: rgba(32, 32, 32, 0.75);
  --NSDexpressionWidth: 512px;
  --NSDbgImageOpacity: 0.25;

  --box-shadow-color: #00000080;
  --black50a: color-mix(in srgb, var(--NSDSmartThemeBorderColor) 30%, transparent);
  --white50a: color-mix(in srgb, var(--SmartThemeUnderlineColor) 60%, rgba(255, 255, 255, 0.5));
  }


  body:has(#bg1) {

    scrollbar-width: thin;
    scrollbar-color: var(--SmartThemeUnderlineColor) var(--SmartThemeBlurTintColor) !important;
    scroll-behavior: smooth !important;
    background-color: var(--NSDSmartThemeBorderColor);
    display: flex;

    --nav-bar-width: 50px;
    --avatar-base-width: 50px;
    --avatar-base-height: 50px;


    --char-panel-with: 0px;
    --settings-panel-width: 0px;
    --authors-note-panel-width: 0px;
    --expression-image-width: 0px;
    --cfgConfig-panel-width: 0px;
    --objectiveExtensionPopout-panel-width: 0px;
    --logprobsViewer-panel-width: 0px;
    --groupMemberListPopout-panel-width: 0px;
    --stcdxcodex: 0px;
    --ctsi-drawer-with: 0px;
    --notebookPanel-width: 0px;
    --gallery-panel-width: 0px;
    --trackerUI-panel-width: 0px;

    --elements-height: calc(100svh - (var(--theme-margin) * 2));
    --drawer-content-panel-wit-img-width: calc(100vw - var(--nav-bar-width) - var(--cfgConfig-panel-width) - var(--objectiveExtensionPopout-panel-width) - var(--expression-image-width) - var(--authors-note-panel-width) - var(--char-panel-width) - var(--settings-panel-width) - var(--stcdxcodex) - var(--trackerUI-panel-width) - var(--notebookPanel-width) - var(--ctsi-drawer-with) - var(--groupMemberListPopout-panel-width) - var(--gallery-panel-width) - var(--theme-margin));
    --theme-margin: 6px;
    }


    #sheld,
    textarea {
      transition: clip-path 200ms !important;
    }

    * {
      box-shadow: unset !important;
      --stcdx--zoomTime: unset !important;
      --stcdx--mapZoneZoomTime: unset !important;
      --stcdx--transitionTime: unset !important;

      --animation-duration: calc(var(--NSDAnimation-duration) * 1s);
      --animation-duration-slow: calc(var(--sheldtransition) * 1s);
      transition-duration: var(--animation-duration) !important;

    }

    html > body.csss--body .csss--isExporting .csss--snippet.csss--selected {
      box-shadow: 0 0 20px var(--SmartThemeQuoteColor) !important;
    }

    hr {
      background-image: unset !important;
      margin: 5px 0;
      height: 1px;
      min-height: 1px;
      border: 0;
      opacity: 0.2;
    }

    .extensions_info hr {
      background-image: linear-gradient(290deg, var(--transparent), var(--SmartThemeBodyColor), var(--transparent)) !important;
    }

    .extensions_info h4 {
      margin-bottom: 0.1em;
    }

    .extensions_info p {
      margin-bottom: 0.2em;
    }
    .checkbox_label {
      align-items: center;
      padding: 2px;
    }

    #WorldInfo .menu_button{
      margin: 0 !important;
    }

    .toast-progress {
      filter:opacity(100%) !important;
    }

    #toast-container>div:hover,
    #toast-container>div,
    .toast-close-button {
      filter:opacity(100%) !important;
    }

    input[type="radio"], 
    input[type="checkbox"] {
      appearance: none;
      background-color: var(--NSDsmartThemeHiglitesColor) !important;
      border-radius: 72px;
      border-style: none;
      flex-shrink: 0;
      height: 16px;
      margin: 0;
      position: relative;
      width: 26px;
      filter:none;
      transform:none;
      border: inherit;
    }

    input[type="radio"]::before, 
    input[type="checkbox"]::before {
      bottom: -6px;
      content: "";
      left: -6px;
      position: absolute;
      right: -6px;
      top: -6px;
    }

    input[type="radio"]::after, 
    input[type="checkbox"]::after {
      transition: all 100ms ease-out;
    }

    input[type="radio"]::after, 
    input[type="checkbox"]::after {
      background-color: #fff !important;
      border-radius: 50%;
      content: "";
      height: 10px;
      left: 3px;
      position: absolute;
      top: 3px;
      width: 10px;
    }

    input[type="radio"], 
    input[type="checkbox"] {
      cursor: default;
    }

    input[type="radio"]:hover, 
    input[type="checkbox"]:hover {
      background-color: var(--white50a) !important;
      transition-duration: 0s;
    }

    input[type="radio"]:checked, 
    input[type="checkbox"]:checked {
      background-color: var(--white50a) !important;
    }

    input[type="radio"]:checked::after, 
    input[type="checkbox"]:checked::after {
      background-color: #fff !important;
      left: 13px;
    }

    input[type="radio"]:focus:not(.focus-visible), 
    input[type="checkbox"]:focus:not(.focus-visible) {
      outline: 0;
    }

    input[type="radio"]:checked:hover, 
    input[type="checkbox"]:checked:hover {
      background-color: var(--black50a) !important;
    }

    .drawer-icon.fa-solid {
      font-size: calc(var(--bottomFormIconSize) * 0.9);
    }

    .drawer-icon {
      color: var(--NSDDrawer-iconColor) !important;
    }

    .drawer-icon.closedIcon {
      opacity: 0.4;
    }

    .custom-drawer-icon, .inline-drawer-icon {
      font-size: calc(var(--mainFontSize) * 1);
    }

    select,
    .mdHotkeys,
    .text_pole,
    .textarea_compact,
    .neo-range-input {
      outline: 1px solid var(--grey5020a);
    }

    #rightSendForm > div:not(.mes_stop), #leftSendForm > div {
      font-weight: 800 !important;
      font-size: calc(var(--bottomFormIconSize) * 0.8);
    }

    #dupeFinderPanel.open {
      z-index: 400 !important;
    }

    body:has(#stcdx--codex.stcdx--active) #WorldInfo {
      max-width: var(--drawer-content-panel-wit-img-width);
    }

    body.charListGrid:has(#right-nav-panel.openDrawer):not(#rm_ch_create_block) {
      --char-panel-width: calc(var(--NSDlistGrid-char-panel-width) * 1px);
    }

    body:has(#right-nav-panel.openDrawer) {
      --char-panel-width: calc(var(--NSDnormal-char-panel-width) * 1px);
    }

    body:has(#rm_ch_create_block:is([style*="display: block; opacity: 1;"])) {
      --char-panel-width: calc(var(--NSDnormal-char-panel-width) * 1px) !important;
    }

    body:has(#right-nav-panel.closedDrawer) {
      --char-panel-width: 0px !important;
    }

    body:has(.stcdx--root.stcdx--codex.stcdx--active) {
      --stcdxcodex: 400px !important;
    }

    body:has(#trackerInterface) {
      --trackerUI-panel-width: 400px !important;
    }

    body:has(#trackerUI) {
      --trackerUI-panel-width: 400px !important;
    }

    body:has(#notebookPanel.flex) {
      --notebookPanel-width: 400px !important;
    }

    body:has(#notebookPanel.maximized) {
      --notebookPanel-width: 0px !important;
    }

    body:has(#left-nav-panel.openDrawer) {
      --settings-panel-width: 360px !important;
    }

    body:has(#expression-image:not([src=""]))  {
      --expression-image-width: var(--NSDexpressionWidth) !important;
    }

    body:has(#expression-image:not([src=""])).waifuMode {
      --expression-image-width: 0px !important;
    }

    body:has(.zoomed_avatar.draggable) {
      --expression-image-width: var(--NSDexpressionWidth) !important;
    }

    body:has(.zoomed_avatar.draggable).waifuMode {
      --expression-image-width: 0vw !important;
    }

    body:not(:has(.zoomed_avatar.draggable)) {
      --expression-image-width: 0px;
    }

    body:has(#expression-image.collapse) {
      --expression-image-width: 0px !important;
    }

    body:has(#ctsi-drawer.maximized) {
      --ctsi-drawer-with: 320px !important;
    }

    body:has(#ctsi-drawer.flexFlowColumn) {
      --ctsi-drawer-with: 0px;
    }

    body:has(#floatingPrompt:is([style*="display: flex; opacity: 1;"])) {
      --authors-note-panel-width: 400px;
    }

    body:has(#floatingPrompt.maximized) {
      --authors-note-panel-width: 0px !important;
    }

    body:has(#cfgConfig:is([style*="display: flex; opacity: 1;"])) {
      --cfgConfig-panel-width: 360px;
    }

    body:has(#objectiveExtensionPopout:is([style*="display: flex;"])) {
      --objectiveExtensionPopout-panel-width: 360px;
    }

    body:has(#cfgConfig.maximized) {
      --cfgConfig-panel-width: 0px !important;
    }

    body:has(#cfgConfig.maximized) {
      --objectiveExtensionPopout-panel-width: 0px !important;
    }

    body:has(#groupMemberListPopout) {
      --groupMemberListPopout-panel-width: 340px;
    }

    body:has(#gallery.draggable) {
      --gallery-panel-width: 340px;
    }

    body:has(#logprobsViewer:is([style*="display: flex; opacity: 1;"])) {
      --expression-image-width: 20vw !important;
    }

    body:has(#logprobsViewer.maximized) {
      --expression-image-width: 0px !important;
    }


    /*********
    ctsi-drawer
    *********/

    #movingDivs > #ctsi-drawer {
      width: calc(var(--ctsi-drawer-with) - var(--theme-margin)) !important;
      right: calc(var(--settings-panel-width) + var(--authors-note-panel-width) + var(--notebookPanel-width)) !important;
